sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */


	//if (input.keywords != null && input.keywords != '')
		//data.items = getCatalogItems(input.keywords);
	data.items = getCatalogItems();

	function getCatalogItems() {
		var sc = new GlideRecord('x_aleen_badgeoff_alerttrackingmoduletable');
		sc.addActiveQuery();
		//sc.addQuery('123TEXTQUERY321', keywords);
		sc.setLimit(10);
		sc.query();
		var results = [];
		while (sc.next()) {
			if (!$sp.canReadRecord(sc)){
				gs.debug("Cannot read Record");
				continue;
			}else{
				gs.debug("Record can be read");
			}


			var item = {};
			item.BadgeId = sc.badgeid.toString();
			results.push(item);
		}
		return results;
	}
})();
