sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    try {
        gs.info("Create User In Alert   :: begin ");
        var r = new sn_ws.RESTMessageV2('x_aleen_badgeoff.GetJWTToken', 'POST');
        var response = r.executeAsync();
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();
        var Token = null;
        var responseObj = JSON.parse(responseBody);
        if (null != responseObj && responseObj.success && null != responseObj.data) {
            Token = "Bearer "+responseObj.data.access_token;
            var requestBody1 = '{ "identity": { "firstName":"' + current.subject.first_name + '", "lastName":"' + current.subject.last_name + '","email":"' + current.subject.email +
                '","masterIdentityId":"' + current.subject.user_name + '"}}';
            var requestBody = requestBody1.toString();
            gs.info("requestBody is " + requestBody);
            var createUserRequest = new sn_ws.RESTMessageV2('x_aleen_badgeoff.Create User In Alert', 'POST');
            createUserRequest.setRequestHeader('Authorization',Token);
            createUserRequest.setRequestBody(requestBody);
            var createUserRequestresponse = createUserRequest.executeAsync();
            var createUserRequestresponseBody = createUserRequestresponse.getBody();
            var createUserRequesthttpStatus = createUserRequestresponse.getStatusCode();
            if (createUserRequesthttpStatus == 200) {
                gs.info(" User created Successfully in The Alert");
            } else {
                gs.info(" User creation is unsuccessfull in Alert");
            }
        }
    } catch (ex) {
        var message = ex.message;
    }
})(current, previous);
