sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    gs.info('setting parent task configuration');
    try {
        if (current.isNewRecord()) {
            current.parent_request_id = parent.number;
        }
        var attachementId = null;
        if (current.user) {
            current.user_name = current.user.name;
        }
        if (current.user && current.user.photo) {
            gs.info('Sys user has Image');
            current.userimage = current.user.photo;
            var userAttachments = new GlideSysAttachment();
            var sysUserImage = userAttachments.getAttachments('ZZ_YYsys_user', current.getValue('user'));
            while (sysUserImage.next())
                if (sysUserImage.getValue('file_name') === 'photo') {
                    gs.log(sysUserImage.getValue('sys_id'));
                    attachementId = sysUserImage;
                    gs.info('attachment_id  from User::' + attachementId);
                }
        } else if (current.userimage) {
            gs.info(' sys user has no Image , uploaded from page');
            var attachment = new GlideSysAttachment();
            var agr = attachment.getAttachments('ZZ_YYx_aleen_badgeoff_user_badges', current.sys_id);
            while (agr.next())
                if (agr.getValue('file_name') === 'userimage') {
                    attachementId = agr.getValue('sys_id');
                    gs.info('attachment_id from user badges  ::' + attachementId);
                }
        }
        current.imageprefix = gs.getProperty("glide.servlet.uri") + attachementId + ".iix";
        var aebadgeSetting = new GlideRecordSecure('x_aleen_badgeoff_configuration');
        aebadgeSetting.query();
        while (aebadgeSetting.next()) {
            current.frontsidehtml = aebadgeSetting.front_side_template;
            current.backsidehtml = aebadgeSetting.back_side_template;
            current.company_name = aebadgeSetting.company_name;
        }
    } catch (ex) {
        var message = ex.message;
        gs.error('message' + message);
    }
})(current, previous);
