sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    gs.info(" Business Rule is invoking API calls  async after clicking UI Action");
    var assignBadgeRequest = '';
    var gBadgeValidTo = '';
    var gDateValidTo = '';
    var badgeValidTo = '';
    var gBadgeValidFrom = '';
    var gDateValidFrom = '';
    var badgeValidFrom = '';
    var requestBody1 = '';
    var requestBody = '';
    var assignBadgeRequestresponse = '';
    var assignBadgeRequestresponseBody = '';
    var assignBadgeRequesthttpStatus = '';
    var assignBadgeresponseObj = '';
    var includeScript = new AEBadgeofficeScript();
    var Token = includeScript.jwt_token;
    assignBadgeRequest = new sn_ws.RESTMessageV2('x_aleen_badgeoff.AssignBadge', 'POST');
    assignBadgeRequest.setRequestHeader('Authorization', Token);
    gs.debug('Endpoint   :: ' + assignBadgeRequest.getEndpoint());
    gBadgeValidTo = new GlideDateTime(current.badge_valid_to.getDisplayValue());
    gDateValidTo = gBadgeValidTo.getDate();
    badgeValidTo = gDateValidTo.getByFormat('dd-MMM-YYYY');
    gBadgeValidFrom = new GlideDateTime(current.badge_valid_from.getDisplayValue());
    gDateValidFrom = gBadgeValidFrom.getDate();
    badgeValidFrom = gDateValidFrom.getByFormat('dd-MMM-YYYY');
    // invoking x_aleen_badgeoff.AssignBadge from Activate UI Action
    if (current.badge_status == 'Inactive' && current.api_requested_for == 'Activate') {
        gs.info("invoking async activate  BR");
        if (null != Token) {
            requestBody1 = '[{  "masterIdentityId":"' + current.user.user_name +
                '", "requestUuid":"' + current.sys_id +
                '","identityAsset":[{ "validFrom": "' + badgeValidFrom +
                '","validTo":"' + badgeValidTo +
                '","assetAccessId":"' + current.badgeid +
                '","action":"ACTIVATE"}]}]';
            requestBody = requestBody1.toString();
            gs.debug("Activate Badge requestBody is " + requestBody);
            assignBadgeRequest.setRequestBody(requestBody);
            assignBadgeRequest.setHttpTimeout(180000);
            assignBadgeRequestresponse = assignBadgeRequest.execute();
            assignBadgeRequestresponseBody = assignBadgeRequestresponse.getBody();
            assignBadgeRequesthttpStatus = assignBadgeRequestresponse.getStatusCode();
            assignBadgeresponseObj = JSON.parse(assignBadgeRequestresponseBody);
            gs.info("Activate Badge response " + JSON.stringify(assignBadgeresponseObj));
            if (assignBadgeRequesthttpStatus == 200 && null != assignBadgeresponseObj && assignBadgeresponseObj.success) {
                gs.info(current.badgeid + " is activated successfully");
                current.badge_status = 'Active';
            } else {
                gs.info(" Error occured while activating  badge  :: " + current.badgeid);
            }
            current.api_requested_for = '';
            current.update();
        }
    }
    // invoking x_aleen_badgeoff.AssignBadge from Deactivate UI Action
    if (current.badge_status == 'Active' && current.api_requested_for == 'Deactivate') {
        gs.info("invoking async  deactivate BR");
        if (null != Token) {
            requestBody1 = '[{  "masterIdentityId":"' + current.user.user_name +
                '", "requestUuid":"' + current.sys_id +
                '","identityAsset":[{ "validFrom": "' + badgeValidFrom +
                '","validTo":"' + badgeValidTo +
                '","assetAccessId":"' + current.badgeid +
                '","action":"DEACTIVATE"}]}]';
            requestBody = requestBody1.toString();
            gs.debug("Assign Badge requestBody is " + requestBody);
            assignBadgeRequest.setRequestBody(requestBody);
            assignBadgeRequest.setHttpTimeout(180000);
            assignBadgeRequestresponse = assignBadgeRequest.execute();
            assignBadgeRequestresponseBody = assignBadgeRequestresponse.getBody();
            assignBadgeRequesthttpStatus = assignBadgeRequestresponse.getStatusCode();
            assignBadgeresponseObj = JSON.parse(assignBadgeRequestresponseBody);
            gs.debug("deactivate badge response " + JSON.stringify(assignBadgeresponseObj));
            if (assignBadgeRequesthttpStatus == 200 && null != assignBadgeresponseObj && assignBadgeresponseObj.success) {
                gs.info(current.badgeid + " is deactivated successfully");
                current.badge_status = 'Inactive';
            } else {
                gs.info(" Error occured while deactivating  badge  :: " + current.badgeid);
            }
            current.api_requested_for = '';
            current.update();
        }
    }
    // invoking x_aleen_badgeoff.AssignBadge from Remove UI Action
    if (current.api_requested_for == 'Remove') {
        gs.info("invoking async  remove BR");
        if (null != Token) {
            requestBody1 = '[{  "masterIdentityId":"' + current.user.user_name +
                '", "requestUuid":"' + current.sys_id +
                '","identityAsset":[{ "validFrom": "' + badgeValidFrom +
                '","validTo":"' + badgeValidTo +
                '","assetAccessId":"' + current.badgeid +
                '","action":"REMOVE"}]}]';
            requestBody = requestBody1.toString();
            gs.debug("requestBody is " + requestBody);
            assignBadgeRequest.setRequestBody(requestBody);
            assignBadgeRequest.setHttpTimeout(180000);
            assignBadgeRequestresponse = assignBadgeRequest.execute();
            assignBadgeRequestresponseBody = assignBadgeRequestresponse.getBody();
            assignBadgeRequesthttpStatus = assignBadgeRequestresponse.getStatusCode();
            assignBadgeresponseObj = JSON.parse(assignBadgeRequestresponseBody);
            gs.debug("remove responseObj is " + JSON.stringify(assignBadgeresponseObj));
            if (assignBadgeRequesthttpStatus == 200 && null != assignBadgeresponseObj && assignBadgeresponseObj.success) {
                current.alert_badge_status = 'Removed';
                gs.info(current.badgeid + " is removed successfully");
            }
            current.api_requested_for = '';
            current.update();
        }
    }
    // invoking x_aleen_badgeoff.AssignBadge from Provision UI Action
    if (current.api_requested_for == 'Provision') {
        gs.info("invoking async  provision BR");
        if (null != Token) {
            requestBody1 = '{  "masterIdentityId":"' + current.user.user_name +
                '", "requestUuid":"' + current.parent_request_id +
                '","identityAsset":[{ "validFrom": "' + badgeValidFrom +
                '","validTo":"' + badgeValidTo +
                '","assetAccessId":"' + current.badgeid +
                '","action":"ADD"}]}';
            requestBody = requestBody1.toString();
            gs.debug("requestBody is " + requestBody);
            assignBadgeRequest.setRequestBody(requestBody);
            assignBadgeRequest.setHttpTimeout(120000);
            assignBadgeRequestresponse = assignBadgeRequest.execute();
            assignBadgeRequestresponseBody = assignBadgeRequestresponse.getBody();
            assignBadgeRequesthttpStatus = assignBadgeRequestresponse.getStatusCode();
            //   if (assignBadgeRequesthttpStatus == 200 && null != assignBadgeresponseObj && assignBadgeresponseObj.success) {
            if (assignBadgeRequesthttpStatus != 400) {
                current.alert_badge_status = 'Provisioned';
                gs.info(current.badgeid + " is assigned successfully");
                gs.debug("BAdge Request  Task ID " + current.parent_request_id);
                if (null != current.parent_request_id && current.parent_request_id != "") {
                    var grbadgeRequestTask = new GlideRecordSecure('x_aleen_badgeoff_badge_request_task');
                    if (grbadgeRequestTask.get('number', current.parent_request_id)) {
                        grbadgeRequestTask.state = 3;
                        hrTaskId = grbadgeRequestTask.parent_task_id;
                        grbadgeRequestTask.update();
                        gs.info("Badge request Closed Successfully ");
                    }
                }
            }
            current.api_requested_for = '';
            current.update();
        }
    }
})(current, previous);
