sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    var currentActivity = current.short_description;
    gs.info("On Creation Of HR Bading Activity:Current Activity:" + currentActivity + " number:" + current.number);

    var badingofficerTask = new GlideRecordSecure('x_aleen_badgeoff_badge_request_task');
    badingofficerTask.assigned_to = current.assigned_to;
    badingofficerTask.subject = current.parent.subject_person;
    badingofficerTask.parent_task_id = current.sys_id;
    badingofficerTask.description = 'Requested Created by HR Task ::' + current.number;
    badingofficerTask.requestdate = new GlideDateTime();
    var idcreated = badingofficerTask.insert();
    gs.info("Inserted record in  badge task");
    gs.info("Badge Request Number created is :: " + idcreated);
    if (badingofficerTask.get(idcreated)) {
        current.rich_description = 'please take action on badge request Created for this activity::' + badingofficerTask.number;
        current.work_notes = 'No action required on this task here';
        current.update();
    }

})(current, previous);
