sys_script_include.script:
var AEBadgeofficeScript = Class.create();
AEBadgeofficeScript.prototype = {
    initialize: function() {
        this.jwt_token = this.getJWT();
    },

    getJWT: function() {
        try {
            var r = new sn_ws.RESTMessageV2('x_aleen_badgeoff.GetJWTToken', 'POST');
            var requestBody1 = '{ "username":"' + gs.getProperty('x_aleen_badgeoff.alert_api_username') + '", "password":"' + gs.getProperty('x_aleen_badgeoff.alert_api_password') + '"}';
            var requestBody = requestBody1.toString();
            r.setRequestBody(requestBody);
            var response = r.execute();
            var responseBody = response.getBody();
            var httpStatus = response.getStatusCode();
            var Token = null;
            var responseObj = JSON.parse(responseBody);
            if (null != responseObj && responseObj.success && null != responseObj.data) {
                Token = 'Bearer ' + responseObj.data.access_token;
                gs.info('Authentication is Successful');
            }
            return Token;
        } catch (ex) {
            var message = ex.message;
            gs.debug('getJWT   :: ' + message);
        }
    },
    syncBadgeTemplates: function() {
        try {
            if (null != this.jwt_token) {
                gs.info(" Token is not Null");
                var templateRequest = new sn_ws.RESTMessageV2('x_aleen_badgeoff.GetBadgeTemplateFromAlertEnterprise', 'POST');
                templateRequest.setRequestHeader('Authorization', this.jwt_token);
                var templateResponse = templateRequest.execute();
                var templateResponseBody = templateResponse.getBody();
                var templateResponseStatus = templateResponse.getStatusCode();
                var templateResponseObject = JSON.parse(templateResponseBody);
                if (null != templateResponseObject && templateResponseObject.success) {
                    var rec = new GlideRecordSecure('x_aleen_badgeoff_configuration');
                    rec.query();
                    if (rec.next()) {
                        gs.info('updated existing x_aleen_badgeoff_configuration');
                        rec.setValue('front_side_template', templateResponseObject.data[0].frontSideBadgeHTML);
                        rec.setValue('back_side_template', templateResponseObject.data[0].backSideBadgeHTML);
                        rec.update();
                    } else {
                        gs.info('inserting new x_aleen_badgeoff_configuration');
                        var newrec = new GlideRecordSecure('x_aleen_badgeoff_configuration');
                        newrec.setValue('front_side_template', templateResponseObject.data[0].frontSideBadgeHTML);
                        newrec.setValue('back_side_template', templateResponseObject.data[0].backSideBadgeHTML);
                        newrec.setValue('company_name', 'Alert Enterprise');
                        newrec.setValue('template_for', 'Employee');
                        newrec.insert();
                    }
                }
            }
        } catch (ex) {
            var message = ex.message;
            gs.debug('syncBadgeTemplates   :: ' + message);
        }
    },
    type: 'AEBadgeofficeScript'
};
