sys_ui_action.script:
var includeScript = new AEBadgeofficeScript();
var Token = includeScript.jwt_token;
if (null != Token) {
    gs.info("Got the Token before making Provision call");
    var assignBadgeRequest = new sn_ws.RESTMessageV2('x_aleen_badgeoff.AssignBadge', 'POST');
    assignBadgeRequest.setRequestHeader('Authorization', Token);
    var gBadgeValidTo = new GlideDateTime(current.badge_valid_to.getDisplayValue());
    var gDateValidTo = gBadgeValidTo.getDate();
    var badgeValidTo = gDateValidTo.getByFormat('dd-MMM-YYYY');
    var gBadgeValidFrom = new GlideDateTime(current.badge_valid_from.getDisplayValue());
    var gDateValidFrom = gBadgeValidFrom.getDate();
    var badgeValidFrom = gDateValidFrom.getByFormat('dd-MMM-YYYY');
    var requestBody1 = '[{  "masterIdentityId":"' + current.user.user_name +
        '", "requestUuid":"' + current.sys_id +
        '","identityAsset":[{ "validFrom": "' + badgeValidFrom +
        '","validTo":"' + badgeValidTo +
        '","assetAccessId":"' + current.badgeid +
        '","action":"REMOVE"}]}]';
    var requestBody = requestBody1.toString();
    gs.info("requestBody is " + requestBody);
    assignBadgeRequest.setRequestBody(requestBody);
    var assignBadgeRequestresponse = assignBadgeRequest.execute();
    var assignBadgeRequestresponseBody = assignBadgeRequestresponse.getBody();
    var assignBadgeRequesthttpStatus = assignBadgeRequestresponse.getStatusCode();
    var assignBadgeresponseObj = JSON.parse(assignBadgeRequestresponseBody);
    gs.info(" De provision RequesthttpStatus is " + assignBadgeRequesthttpStatus);
    gs.info(" De provision responseObj is " + JSON.stringify(assignBadgeresponseObj));
    if (assignBadgeRequesthttpStatus == 200 && null != assignBadgeresponseObj && assignBadgeresponseObj.success) {
        current.alert_badge_status = 'Removed';
        current.update();
    }
}
